  return (
    <Modal onClose={props.onClick}>
      <div className="modal_wrapper">
       <div className="modal_header">
         <a>Info zu den Statistiken</a>
       </div>
       <div className="modal_body">
         Warum die Stats? (Also dass man den zeigen wioll, dass man nicht nur sabbelt)
         Was Zeigen die Stats? 
         Worauf basieren die? Letzten 2 Saisons alle Spiele der Ligen und von footystats die Daten
       </div>

      </div>
    </Modal>
  );
}

export default ModalInfo;
