import React, { useRef, useState } from "react";
import NavBar from "./NavBar";
import "./Main.css";
import HomeDescription from "./HomeDescription";
import "@fontsource/dm-sans";
import CarouselComponent from "./CarouselComponent";
import Anleitungen from "./Anleitungen";
import Diagrams from "./Diagrams";
import ModalStats from "./ModalStats";
import { FcFilledFilter, FcEmptyFilter } from "react-icons/fc";
import Home from "./Home";

function Main() {
  const homeRef = useRef();
  const quotenRef = useRef();
  const whyRef = useRef();
  const tutRef = useRef();
  const [isActive, setIsActive] = useState(false);

  const onClick = () => setIsActive(!isActive);

  const hideModalHandler = () => {
    setIsActive(false);
  };

  function handleQuotenClick() {
    quotenRef.current.scrollIntoView({ behavior: "smooth" });
  }

  function handleWhyClick() {
    whyRef.current.scrollIntoView({ behavior: "smooth" });
  }

  const date = new Date();
  const dateOptions = {
    weekday: "short",
    year: "numeric",
    month: "2-digit",
    day: "numeric",
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <div className="main" ref={homeRef}>
      <NavBar
        homeRef={homeRef}
        quotenRef={quotenRef}
        whyRef={whyRef}
        tutRef={tutRef}
      />

      <Home
        handleQuotenClick={handleQuotenClick}
        handleWhyClick={handleWhyClick}
        quotenRef={quotenRef}
      />

      {/* <div className="section">
        <div classname="section__wrapper">
          <div className="section__header">
            <a className="section__header__text" style={{ paddingTop: "3%" }}>
              Beste Quoten
            </a>
          </div>
          <a className="date__text">
            {date.toLocaleDateString("de-DE", dateOptions)}
          </a>
          <div className="section_items">
            <MatchItem />
            <MatchItem />
            <MatchItem />
            <MatchItem />
            <MatchItem />
            <MatchItem />
            <MatchItem />
            <MatchItem />

          </div>
        </div>
      </div> */}

      <div className="section">
        <div classname="section__wrapper">
          <div className="section__header">
            <a className="section__header__text" style={{ paddingTop: "3%" }}>
              Statistiken
            </a>
          </div>

          <div className="section_items">
            <div className="odds_market">
              <button className="menu-trigger" onClick={onClick}>
                <span>Märkte</span>
                {isActive ? (
                  <FcFilledFilter size={20} />
                ) : (
                  <FcEmptyFilter size={20} />
                )}
              </button>

              <span className="market_name">Over/Under 2,5</span>
            </div>

            {isActive && <ModalStats onClick={hideModalHandler} />}
            <Diagrams />
          </div>
        </div>
      </div>

      <div className="section2" ref={whyRef}>
        <div classname="section__wrapper">
          <div className="section__header">
            <a
              className="section__header__text"
              style={{ color: "white", paddingTop: "1%" }}
            >
              Warum BetSmart?
            </a>
          </div>

          <div className="section_items">
            <CarouselComponent />
          </div>
        </div>
      </div>

      <div className="section">
        <a ref={tutRef} style={{ color: "white" }}>
          Pinnacle Sports, AsianConnect
        </a>
        <div classname="section__wrapper">
          <div className="section__header">
            <a className="section__header__text" style={{ paddingTop: "3%" }}>
              Anleitungen
            </a>
          </div>

          <div className="section_items">
            <Anleitungen />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Main;
